import * as React from 'react';
import { useState } from 'react';
import { FilePond } from 'react-filepond'

import "filepond/dist/filepond.min.css";
import { FilePondErrorDescription, FilePondFile } from 'filepond';

const FileUpload = (): JSX.Element => {
  const [file, setFile] = useState<FilePondFile>();

  const onProcessFile = (error: FilePondErrorDescription | null, file: FilePondFile) => {
    if(error){
      console.error('An error occurred in the attachment upload. Please try again.');
    }
    
    setFile(file);
    
  };

    return (
      <div>
        <FilePond
          onprocessfile={onProcessFile}
         server={{url: 'http://localhost:8080/api/attachments', process: '/upload', revert: null, restore: null, load: null, fetch: null }}
         name="file"
         labelIdle='Drag & Drop your files or <span class="filepond--label-action">Browse</span>' 
        >
        </FilePond>
        {file && (<p>{file.filename}</p>)}
      </div>
      
    );
}

export default FileUpload;
